import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Login from "./components/Login";
import NotFound from "./components/NotFound";
import SmartGoalForm from "./components/SmartGoalForm";
import Navbar from "./components/Navbar";
import PreviousValidations from "./components/PreviousValidations";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem("access_token");
    setIsAuthenticated(!!token); 
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  return (
    <Router>
      {isAuthenticated && <Navbar />} {/* Navbar will always be there if authenticated */}
      <Routes>
        <Route path="/" element={isAuthenticated ? <Navigate to="/smarthr-form" replace /> : <Navigate to="/login" replace />} />
        <Route path="/login" element={isAuthenticated ? <Navigate to="/smarthr-form" replace /> : <Login onLogin={handleLogin} />} />
        <Route path="/smarthr-form" element={isAuthenticated ? <SmartGoalForm onLogout={handleLogout} /> : <Navigate to="/login" replace />} />
        <Route path="/previous-validations" element={isAuthenticated ? <PreviousValidations /> : <Navigate to="/login" replace />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Router>
  );
}

export default App;
