import React, { useState, useEffect } from "react";

const PreviousValidations = ({ apiUrl, token }) => {
  const [userGoals, setUserGoals] = useState([]);

  useEffect(() => {
    const fetchUserGoals = async () => {
      try {
        const response = await fetch(`${apiUrl}/api/user-goals/`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch user goals");
        }

        const data = await response.json();
        setUserGoals(data);
      } catch (error) {
        console.error("Error:", error);
        setUserGoals([]);
      }
    };

    fetchUserGoals();
  }, [apiUrl, token]);

  return (
    <div className="previous-validations-container">
      <h2>Previous Validations</h2>
      <table className="validations-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Goal</th>
            <th>Response</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {userGoals.length > 0 ? (
            userGoals.map((goal, index) => (
              <tr key={goal.id}>
                <td>{index + 1}</td>
                <td>{goal.goal}</td>
                <td>{goal.response}</td>
                <td>
                  <button className="edit-btn">Edit</button>
                </td>
                <td>
                  <button className="delete-btn">Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No goals found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default PreviousValidations;
