import React, { useState } from "react";
import "./SmartGoalForm.css";

const SmartGoalForm = () => {
  const apiUrl = process.env.REACT_APP_API_URL;
  const token = sessionStorage.getItem("access_token")
  const [formData, setFormData] = useState({
    goal: "",
    measureOfSuccess: "",
    kpiMetrics: "",
    outcomeDefined: "",
    quantifiableObjective: "",
    skillsAvailable: "",
    obstaclesConsidered: "",
    thrustArea: "",
    startDate: "",
    endDate: ""
  });

  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);


  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };


  const handleSubmit = async (event) => {
    event.preventDefault();
    setMessage("");
    setLoading(true);
    if (formData.quantifiableObjective > 100) {
      setMessage("Quantifiable Objective cannot exceed 100%.");
      return;
    }

    const formattedData = {
      goal: formData.goal,
      measure_of_success: formData.measureOfSuccess,
      kpi_metrics: formData.kpiMetrics,
      outcome_defined: formData.outcomeDefined,
      quantifiable_objective: formData.quantifiableObjective,
      skills_available: formData.skillsAvailable,
      obstacles_considered: formData.obstaclesConsidered,
      thrust_area: formData.thrustArea,
      start_date: formData.startDate,
      end_date: formData.endDate,
    };

    try {
      const response = await fetch(`${apiUrl}/api/submit-goal/`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formattedData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        setMessage(`Error: ${errorText}`);
        setLoading(false);
        return;
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let responseText = "";
      setLoading(false);
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });

        if (chunk.includes("[DONE]")) break;

        responseText += chunk;
        setMessage((prev) => prev + chunk);
      }


      // setFormData({
      //   goal: "",
      //   measureOfSuccess: "",
      //   outcomeDefined: "",
      //   kpiMetrics: "",
      //   quantifiableObjective: "",
      //   skillsAvailable: "",
      //   obstaclesConsidered: "",
      //   thrustArea: "",
      //   startDate: "",
      //   endDate: "",
      // });

    } catch (error) {
      console.error("Error submitting form:", error.message);
      setMessage("An error occurred while submitting the form.");
    }
  };


  return (
    <div className="smart-form-container">
      <h2>SMART Goal Submission</h2>

      <form onSubmit={handleSubmit} className="smart-form">

        <label>Goal:</label>
        <textarea name="goal" value={formData.goal} onChange={handleChange} rows="3" required></textarea>

        <label>Measure of Success:</label>
        <textarea name="measureOfSuccess" value={formData.measureOfSuccess} onChange={handleChange} rows="3" required></textarea>

        <label>What metrics or KPIâ€™s will be used to evaluate the achievement?</label>
        <textarea name="kpiMetrics" value={formData.kpiMetrics} onChange={handleChange} rows="3" required></textarea>

        <label>Can you clearly define the outcome or result?</label>
        <select name="outcomeDefined" value={formData.outcomeDefined} onChange={handleChange} required>
          <option value="">Select</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>

        <label>Can the objective be quantified (Nos., %ages etc.)?</label>
        <input type="number" name="quantifiableObjective" value={formData.quantifiableObjective} onChange={handleChange} required min={0} max={100} step={0.10} />

        <label>Are the necessary skills, knowledge, and expertise available to achieve this objective?</label>
        <select name="skillsAvailable" value={formData.skillsAvailable} onChange={handleChange} required>
          <option value="">Select</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>

        <label>Have you considered any potential obstacles/challenges?</label>
        <select name="obstaclesConsidered" value={formData.obstaclesConsidered} onChange={handleChange} required>
          <option value="">Select</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>

        <label>Choose the Thrust Area this objective aligns with:</label>
        <select name="thrustArea" value={formData.thrustArea} onChange={handleChange} required>
          <option value="">Select</option>
          <option value="Innovation">Innovation</option>
          <option value="Customer Satisfaction">Customer Satisfaction</option>
          <option value="Operational Efficiency">Operational Efficiency</option>
        </select>

        <label>Start Date of Activity:</label>
        <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} required />

        <label>End Date of Activity:</label>
        <input type="date" name="endDate" value={formData.endDate} onChange={handleChange} required />
        {/* <div>
            {message && <p className="message">{message}</p>}
        </div> */}
        <div className="response">
          {/* {message.split("\n\n").map((line, index) => (
            <p key={index}>{line}</p>
          ))} */}
          {/* 
          {message.split("\n\n").map((line, index) => {
            const keywords = [
              "Specific ity", "Me as ur ability", "Ach iev ability",
              "Re levance", "Time -B ound", "Overall"
            ];

            let updatedLine = line;

            keywords.forEach(keyword => {
              const cleanedKeyword = keyword.replace(/\s+/g, "");
              const regex = new RegExp(`\\*\\*\\s*${keyword.replace(/\s+/g, "\\s*")}\\s*:\\s*\\*\\*`, "gi");
              updatedLine = updatedLine.replace(regex, `**${cleanedKeyword}:**`);
            });

            return (
              <p key={index}>
                {updatedLine.split(/(\*\*[^*]+\*\*|\b\d+(\.\d+)?%\b)/g).map((part, i) => {
                  if (!part) return null;

                  if (typeof part === "string" && part.startsWith("**") && part.endsWith("**")) {
                    return <strong key={i}>{part.slice(2, -2).trim()}</strong>;
                  }

                  if (typeof part === "string" && /\b\d+(\.\d+)?%\b/.test(part)) {
                    const percentage = parseFloat(part.replace("%", ""));
                    const color = percentage < 75 ? "red" : "green";
                    return <span key={i} style={{ color, fontWeight: "bold" }}>{part}</span>;
                  }

                  return part;
                })}
              </p>
            );
          })} */}

{loading ? (
            <p>Loading...</p> 
          ) : (
          message.split("\n\n").map((line, index) => {
            const keywords = [
              "Specific ity", "Me as ur ability", "Ach iev ability",
              "Re levance", "Time -B ound", "Overall"
            ];

            let updatedLine = line;

            keywords.forEach(keyword => {
              const cleanedKeyword = keyword.replace(/\s+/g, "");
              const regex = new RegExp(`\\*\\*\\s*${keyword.replace(/\s+/g, "\\s*")}\\s*:\\s*\\*\\*`, "gi");
              updatedLine = updatedLine.replace(regex, `**${cleanedKeyword}:**`);
            });

            return (
              <p key={index}>
                {updatedLine.split(/(\*\*[^*]+\*\*|\d+\s?%)/g).map((part, i) => {
                  if (!part) return null;

                  if (part.trim().startsWith("**") && part.trim().endsWith("**")) {
                    return <strong key={i}>{part.trim().slice(2, -2)}</strong>;
                  }

                  if (/^\d+\s?%$/.test(part.trim())) {
                    const percentage = parseFloat(part.replace("%", "").trim());
                    const color = percentage < 75 ? "red" : "green";

                    return (
                      <span key={i} style={{ color: color, fontWeight: "bold" }}>
                        {part.trim()}
                      </span>
                    );
                  }

                  return part;
                })}
              </p>
            );
          }))}



        </div>
        <button type="submit" className="submit-btn">Submit Goal</button>

      </form>
    </div>
  );
};

export default SmartGoalForm;
