import React from "react";
import { useNavigate } from "react-router-dom";
import "./Navbar.css";
import lntlogo from "../assets/L_T_PES_-_Linear_Logo_-_Black-removebg-preview.png";

const Navbar = ({ onLogout }) => {
  const navigate = useNavigate();
  const apiUrl = process.env.REACT_APP_API_URL


  const handleLogout = async () => {
    const refreshToken = sessionStorage.getItem("refresh_token");

    if (!refreshToken) {
        console.error("No refresh token found");
        sessionStorage.clear();
        navigate("/login");
        return;
    }

    try {
        const response = await fetch(`${apiUrl}/api/logout/`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${sessionStorage.getItem("access_token")}`,
            },
            body: JSON.stringify({ refresh_token: refreshToken }),
        });

        if (response.ok) {
            sessionStorage.clear(); 
            onLogout();
            navigate("/login"); 
        } else {
            console.error("Logout failed");
        }
    } catch (error) {
        console.error("Error logging out:", error);
    }
};

  return (
    <nav className="navbar">
      <div className="nav-logo" >
      <img src={lntlogo} alt="SmartHR Logo" className="logo-img" />
      </div>
      <ul className="nav-links">

        <li className="previous-validation-btn" onClick={handleLogout}>Previous Validations</li>
        <li className="logout-btn" onClick={handleLogout}>Logout</li>
      </ul>
    </nav>
  );
};

export default Navbar;
