import React, { useState, useEffect } from "react";
import "./PreviousValidations.css"; 
import { useNavigate } from "react-router-dom";


const PreviousValidations = () => {
  const [userGoals, setUserGoals] = useState([]);
  const token = sessionStorage.getItem("access_token");
  const apiUrl = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();


  useEffect(() => {
    fetchUserGoals();
  }, [apiUrl, token]);

  const fetchUserGoals = async () => {
    try {
      const response = await fetch(`${apiUrl}/api/user-goals/`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch user goals");
      }

      const data = await response.json();
      setUserGoals(data);
    } catch (error) {
      console.error("Error:", error);
      setUserGoals([]);
    }
  };

  const handleDelete = async (goalId) => {
    if (!window.confirm("Are you sure you want to delete this goal?")) return;


    try {
      const response = await fetch(`${apiUrl}api/user-goals/${goalId}/delete/`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });


      if (response.ok) {
        alert("Goal deleted successfully!");

        setUserGoals((prevGoals) => {
          const updatedGoals = prevGoals.filter((goal) => goal.id !== goalId);
          console.log("Updated Goals After Deletion:", updatedGoals); 
          return updatedGoals;
        });
      } else {
        alert("Failed to delete goal.");
      }
    } catch (error) {
      console.error("Error deleting goal:", error);
      alert("Error deleting goal. Check the console for details.");
    }
  };


  return (
    <div className="previous-validations-container">
      <h2 className="table-heading">Previous Validations</h2>
      <table className="validations-table">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Goal</th>
            <th>Response</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {userGoals.length > 0 ? (
            userGoals.map((goal, index) => (
              <tr key={goal.id}>
                <td>{index + 1}</td>
                <td className="goal-text">{goal.goal}</td>
                <td className="response-text">{goal.response}</td>
                <td>
                  <button className="edit-btn"onClick={() => navigate(`/update-goal/${goal.id}`)}>Edit</button>
                </td>
                <td>
                  <button className="delete-btn" onClick={() => handleDelete(goal.id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="no-data">No goals found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default PreviousValidations;
